    {
        return _context.NguoiDung.Where(x => x.TaiKhoan == tk).FirstOrDefault();
    }
    List<NguoiDung> getTop5USER()
    {
        return _context.NguoiDung.OrderByDescending(x => x.VaiTro == "USER").Take(3).ToList();
