        {
            return _context.CthoiDap.Where(x =>x.MaHoiDap == ma).Take(5).ToList();
        }
    DanhMucHoiDap getDMbyMA(int ma)
    {
        return _context.DanhMucHoiDap.Where(x => x.MaDanhMuc == ma).FirstOrDefault();
    }
